import { IEventEmitter } from './IEventEmitter';
import { ICamera } from './ICamera';
import { IGimbal } from './IGimbal';

export module constants {
    export module commands {
		export const CONNECT_DRONE: string = 'connect-drone';
		export const DISCONNECT_DRONE: string = 'disconnect-drone';
    }
	export module events {
		// connection state events
		export const DISCONNECTED: string = 'disconnected';
        export const CONNECTED: string = 'connected';
        export const UNREACHABLE: string = 'unreachable';
	}
}

/**
 * The actual drone.
 */
