import * as Serialization from '../../utility/Serialization';

export interface ISettingFactory extends Serialization.ICoding {
    create(name: string, value: number) : ISetting;
}

export interface ISetting {
    id: string;
    name: string;
    value: number;

    setValue(value: number): Promise<number>;

    // ICoding implementation
    __type__: string;
    initWithCoder(coder: Serialization.IDecoder) : void;
    encodeWithCoder(coder: Serialization.IEncoder) : void;
}

export default ISetting;
