export enum FlightMode {
    /**
     * Self levels the roll and pitch axis.
     */
    Stabilize,
    /**
     * Stabilize mode + maintains altitude.
     */
    AltHold,
    /**
     * Stabilize, AltHold modes + maintains GPS location.
     */
    Loiter,
    /**
     * Flys to guided waypoints.
     */
    Guided,
    /**
     * Orbits a central lat/long.
     */
    Orbit,
    /**
     * Follows pre-programmed mission scripts.
     */
    Auto,
    /**
     * Return to launch.
     */
    RTL,
    /**
     * Custom/Unknown
     */
    Custom
}

export default FlightMode;
