/**
 * The configuration of the drone.
 */
export interface IDroneConfiguration {
	id: string;
    name: string;
    autopilot: {
		type: string;
		transport: {
			type: string;
			settings?: any;
		};
	};
};

/**
 * Information about where the drone lives, and its configuration.
 */
export interface IDroneMetadata {
    id: string;
    droneHostId: string;
    configuration: IDroneConfiguration;
	checkoutState: {
		isCheckedOut: boolean;
		sessionId: string;
	};
}
