import { IServerNodeClient } from './common/IServerNodeClient';
import { ServerNodeClientController } from './common/ServerNodeClientController';
import { IOrbit, constants as IOrbitConstants } from '@dronesense/core/lib/common/IOrbit';
import { DSError } from '@dronesense/core/lib/common/DSError';

export class Orbit extends ServerNodeClientController implements IOrbit {
    constructor(serverNodeClient: IServerNodeClient) {
        super(serverNodeClient);
    }

    orbit(lattitude: number, longitude: number, altitude: number, radius: number, isClockwise: boolean, angularVelocity: number): Promise<void> {
        return this.sendCommand<void>(IOrbitConstants.commands.ORBIT, lattitude, longitude, altitude, radius, isClockwise, angularVelocity);
    }
}
