import { IServerNodeClient, ServerNodeClient } from './common/IServerNodeClient';
import { IDroneSenseService, constants as IDroneSenseServiceConstants } from '@dronesense/core/lib/common/IDroneSenseService';
import DroneSenseService from './DroneSenseService';

// start the drone service
export interface IClient {
    createClient(address: string): IDroneSenseService;
}

var DS: IClient = {
    createClient(address: string): IDroneSenseService {
        return new DroneSenseService(ServerNodeClient.create(address));
    }
};

export default DS;
