// import BackboneEvents = require('backbone-events-standalone');

/**
 * Note: Classes that implement this must mixin the 'backbone-events-standalone' module.
 */

export interface IEventEmitter {
	// Events
	on(eventName: string, callback?: Function, context?: any): any;
	once(events: string, callback: Function, context?: any): any;
	off(eventName?: string, callback?: Function, context?: any): any;
}

export interface IEventEmitterInternal extends IEventEmitter {
	trigger(eventName: string, ...args: any[]): any;
}

export default IEventEmitter;
