import { ISetting } from './entities/ISetting';

export module constants {
	export module commands {
        export const REFRESH_SETTINGS: string = 'refresh-settings';
        export const GET_SETTINGS: string = 'get-settings';
        export const SET_VALUE: string = 'set-value';
	}
	export module events {
        export const SETTING_VALUE_CHANGED: string = 'setting-value-changed';
        export const SETTINGS_REFRESHED: string = 'settings-refreshed';
    }
}

export interface ISettingDelegate {
    setValue(id: string, value: number): Promise<number>;
}

export interface ISettingsManager {
    /**
     * Refreshes and returns settings from flight controller.
     */
    refresh(): Promise<Array<ISetting>>;

    /**
     * Returns server-cached settings from flight controller.
     */
    getSettings(): Promise<Array<ISetting>>;
}

export default ISettingsManager;
