/**
 * DJI Mobile SDK Values (Android)
 * DJIFlightController
 *  getCurrentState(): DJIFlightControllerDataType.DJIFlightControllerCurrentState
 *
 * DJIFlightControllerDataType.DJIFlightControllerCurrentState
 *  getAttitude(): DJIFlightControllerDataType.DJIAttitude
 *
 * DJIFlightControllerDataType.DJIAttitude
 *  pitch: double
 *  roll: double
 *  yaw: double
 *
 * DJIFlightControllerDataType.DJIVirtualStickFlightControlData
 *  // Velocity, or angle/altitude
 *  getPitch(): float
 *  getRoll(): float
 *  getVerticalThrottle(): float
 *  getYaw(): float
 */

export interface IAttitude {
	roll: number;
	pitch: number;
	yaw: number;
	rollspeed: number;  // DJI/MAVLink not the same?
	pitchspeed: number; // DJI/MAVLink not the same?
	yawspeed: number;   // DJI/MAVLink not the same?
}

export default IAttitude;
