/**
 * Waypoints used by Guided mode.
 */
export interface IGuidedWaypoint {
    /**
     * @property {string} name - the unique name of the waypoint.
     */
    name: string;

    /**
     * @property {number} lattitude - the lattitude of the waypoint.
     */
    lattitude: number;

    /**
     * @property {number} longitude - the longitude of the waypoint.
     */
    longitude: number;

    /**
     * @property {number} altitude - the altitude of the waypoint.
     */
    altitude: number;

    /**
     * @property {number} speed - the speed to apply before traveling to the waypoint.
     */
    speed?: number;

    /**
     * @property {boolean} isReached - true if this waypoint has been reached.
     */
    isReached?: boolean;
}

export default IGuidedWaypoint;
